import React, { useState, useEffect } from 'react';

const Comparison = ({ feed1, feed2 }) => {
    const [allData, setAllData] = useState([]);
    const metrics = ["likes", "posts", "toxicity_score", "positivity_score", "commercial_link_score", "poster_diversity_score"];

    useEffect(() => {
        fetch("http://127.0.0.1:5000/api/normalized_data")
            .then((response) => response.json())
            // .then(data => setAllData(data))
            .then(data => {
                console.log(data);
                setAllData(data);
            })
            .catch(error => console.error("Error fetching data:", error));
    }, []);

    useEffect(() => {
        console.log("Feed1:", feed1);
        console.log("Feed2:", feed2);
        console.log("All:", allData);
    }, [feed1, feed2]);

    const getColor = (value, metric) => {
        if (!allValues.length) return "white"; // Default when no data

        // Sort all values and find which percentile `value` falls into
        const sorted = [...allValues].sort((a, b) => a - b);
        const index = sorted.findIndex(v => v >= value);

        const percentile = (index / sorted.length) * 100;

        // Assign colors based on percentile groups
        if (percentile >= 90) return "darkgreen";  // Top 10% (best)
        if (percentile >= 80) return "lightgreen";
        if (percentile >= 60) return "yellow";
        if (percentile >= 40) return "orange";
        if (percentile >= 20) return "orangered";
        return "red"; // Bottom 20% (worst)
    };

    return (
        <div>
            {feed1 && feed2 ? (
                <table border="1" style={{ width: "100%", textAlign: "center" }}>
                    <thead>
                        <tr>
                            <th>Metric</th>
                            <th>{feed1.feed_id}</th>
                            <th>{feed2.feed_id}</th>
                        </tr>
                    </thead>
                    <tbody>
                        {metrics.map(metric => {
                            const allMetricValues = allData.map(feed => feed[metric]);

                            return (
                                <tr key={metric}>
                                    <td>{metric.replace("_", " ")}</td>
                                    <td style={{ backgroundColor: getColor(feed1[metric], allMetricValues) }}>
                                        {feed1[metric].toFixed(2)}
                                    </td>
                                    <td style={{ backgroundColor: getColor(feed2[metric], allMetricValues) }}>
                                        {feed2[metric].toFixed(2)}
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            ) : (
                <p>Please select two feeds to compare.</p>
            )}
        </div>
    );
};

export default Comparison;
