import React from 'react';
import './Comparison.css'; // Import the CSS file

const getColorIndicator = (value, type) => {
  if (type === "toxicity") {
    return value > 0.5 ? 'red' : 'green';
  }
  if (type === "positivity") {
    return value > 0.7 ? 'green' : 'red';
  }
  return 'yellow';
};

const Comparison = ({ feed1, feed2 }) => {
  return (
    <div className="comparison-container">
      <div className="comparison-card">
        <h2>Feed 1</h2>
        <div className="metric">
          <span>Toxicity Score: {feed1.toxicity_score}</span>
          <span className={`indicator ${getColorIndicator(feed1.toxicity_score, 'toxicity')}`} />
        </div>
        <div className="metric">
          <span>Positivity Score: {feed1.positivity_score}</span>
          <span className={`indicator ${getColorIndicator(feed1.positivity_score, 'positivity')}`} />
        </div>
        <div className="metric">
          <span>Commercial Link Score: {feed1.commercial_link_score}</span>
          <span className={`indicator ${getColorIndicator(feed1.commercial_link_score, 'neutral')}`} />
        </div>
        <div className="metric">
          <span>Poster Diversity Score: {feed1.poster_diversity_score}</span>
          <span className={`indicator ${getColorIndicator(feed1.poster_diversity_score, 'neutral')}`} />
        </div>
      </div>

      <div className="comparison-card">
        <h2>Feed 2</h2>
        <div className="metric">
          <span>Toxicity Score: {feed2.toxicity_score}</span>
          <span className={`indicator ${getColorIndicator(feed2.toxicity_score, 'toxicity')}`} />
        </div>
        <div className="metric">
          <span>Positivity Score: {feed2.positivity_score}</span>
          <span className={`indicator ${getColorIndicator(feed2.positivity_score, 'positivity')}`} />
        </div>
        <div className="metric">
          <span>Commercial Link Score: {feed2.commercial_link_score}</span>
          <span className={`indicator ${getColorIndicator(feed2.commercial_link_score, 'neutral')}`} />
        </div>
        <div className="metric">
          <span>Poster Diversity Score: {feed2.poster_diversity_score}</span>
          <span className={`indicator ${getColorIndicator(feed2.poster_diversity_score, 'neutral')}`} />
        </div>
      </div>
    </div>
  );
};

export default Comparison;
