import React, { useState, useEffect } from 'react';

// Helper function to calculate percentile group
const getPercentileGroup = (value, data) => {
  const sortedData = data.slice().sort((a, b) => a - b);
  const index = sortedData.indexOf(value);
  const percentile = (index / (sortedData.length - 1)) * 100;

  const group = Math.floor(percentile / 10);  // 0-9 range for percentiles
  const colors = [
    'red', 'orange', 'yellow', 'lightgreen', 'green',
    'lightblue', 'blue', 'purple', 'violet', 'indigo'
  ];
  return colors[group];
};

const Comparison = () => {
  const [feeds, setFeeds] = useState([]);
  const [selectedFeeds, setSelectedFeeds] = useState([]);

  // Fetch feed data
  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/normalized_feeds")
      .then((res) => res.json())
      .then((data) => {
        setFeeds(data.feeds);  // Assuming the feeds are in the "feeds" key
      })
      .catch((err) => console.error("Error fetching data:", err));
  }, []);

  const handleFeedSelect = (feed) => {
    if (selectedFeeds.length < 2) {
      setSelectedFeeds([...selectedFeeds, feed]);
    }
  };

  // Define the metrics to compare
  const metrics = [
    'toxicity_score',
    'positivity_score',
    'commercial_link_score',
    'poster_diversity_score'
  ];

  return (
    <div>
      <h1>Select 2 Feeds to Compare</h1>
      <div className="feed-selector">
        {feeds.map((feed, index) => (
          <div
            key={index}
            className={`feed-card ${selectedFeeds.includes(feed) ? 'selected' : ''}`}
            onClick={() => handleFeedSelect(feed)}
          >
            <h2>{feed.category}</h2>
            <p>{feed.likes} Likes</p>
            <p>{feed.posts} Posts</p>
          </div>
        ))}
      </div>

      {selectedFeeds.length === 2 && (
        <table>
          <thead>
            <tr>
              <th>Metric</th>
              <th>Feed 1</th>
              <th>Feed 2</th>
            </tr>
          </thead>
          <tbody>
            {metrics.map((metric) => (
              <tr key={metric}>
                <td>{metric.replace(/_/g, ' ').toUpperCase()}</td>
                <td
                  style={{
                    backgroundColor: getPercentileGroup(
                      selectedFeeds[0][metric],
                      feeds.map(f => f[metric])
                    ),
                  }}
                >
                  {selectedFeeds[0][metric]}
                </td>
                <td
                  style={{
                    backgroundColor: getPercentileGroup(
                      selectedFeeds[1][metric],
                      feeds.map(f => f[metric])
                    ),
                  }}
                >
                  {selectedFeeds[1][metric]}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Comparison;
