import React from "react";
import { ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";

const PoliticalCompass = ({ feeds }) => {
  return (
    <div className="w-full h-96">
      <ResponsiveContainer width="100%" height="100%">
        <ScatterChart
          margin={{ top: 20, right: 30, left: 30, bottom: 20 }}
        >
          {/* Grid and axis settings */}
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            type="number" 
            dataKey="xValue" 
            domain={[-1, 1]} 
            tick={{ fontSize: 12 }} 
            label={{ value: "Dimension X", position: "bottom", offset: 0 }}
          />
          <YAxis 
            type="number" 
            dataKey="yValue" 
            domain={[-1, 1]} 
            tick={{ fontSize: 12 }} 
            label={{ value: "Dimension Y", angle: -90, position: "left", offset: 10 }}
          />
          <Tooltip cursor={{ strokeDasharray: "3 3" }} />

          {/* Plot the feeds */}
          <Scatter name="Feeds" data={feeds} fill="#8884d8" />

        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PoliticalCompass;
