import React, { useState } from "react";
import { ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Label, Line } from "recharts";

// Function to calculate linear regression (y = mx + b)
const linearRegression = (data, xKey, yKey) => {
  const n = data.length;
  const sumX = data.reduce((acc, point) => acc + point[xKey], 0);
  const sumY = data.reduce((acc, point) => acc + point[yKey], 0);
  const sumXY = data.reduce((acc, point) => acc + point[xKey] * point[yKey], 0);
  const sumX2 = data.reduce((acc, point) => acc + point[xKey] * point[xKey], 0);

  // Slope (m) and intercept (b)
  const m = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
  const b = (sumY - m * sumX) / n;

  return { m, b };
};

const PoliticalCompass = ({ feeds }) => {
  const [xAxis, setXAxis] = useState("toxicity_score"); // Default X-axis
  const [yAxis, setYAxis] = useState("positivity_score"); // Default Y-axis

  const dimensions = [
    "likes", 
    "posts", 
    "toxicity_score", 
    "positivity_score", 
    "commercial_link_score", 
    "poster_diversity_score"
  ];

  // Function to capitalize only the first letter of each word
  const formatLabel = (label) => {
    if (typeof label !== 'string') return label; // Return label as is if it's not a string

    return label
      .split('_') // Split the string by underscores
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()) // Capitalize the first letter and make the rest lowercase
      .join(' '); // Join the words back with spaces
  };


  // Format values to percentages
  const formatPercentage = (value) => {
    return `${(value * 100).toFixed(0)}%`;
  };

  // Calculate linear regression line points
  const { m, b } = linearRegression(feeds, xAxis, yAxis);

  // Generate points for the regression line
  const lineData = [
    { [xAxis]: 0, [yAxis]: b }, // Start of the line
    { [xAxis]: 1, [yAxis]: m + b } // End of the line
  ];

  return (
    <div className="p-6 max-w-4xl mx-auto border rounded-lg bg-white shadow-lg">
      <h2 className="text-2xl font-semibold mb-6 text-center text-gray-800">Feed Political Compass</h2>

      {/* Dropdowns for selecting X and Y axes */}
      <div className="flex gap-6 justify-center mb-6">
        <label className="flex flex-col items-center">
          <span className="font-medium text-gray-700">X-Axis:</span>
          <select 
            value={xAxis} 
            onChange={(e) => setXAxis(e.target.value)} 
            className="mt-2 px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
          >
            {dimensions.map((dim) => (
              <option key={dim} value={dim}>{formatLabel(dim)}</option>
            ))}
          </select>
        </label>

        <label className="flex flex-col items-center">
          <span className="font-medium text-gray-700">Y-Axis:</span>
          <select 
            value={yAxis} 
            onChange={(e) => setYAxis(e.target.value)} 
            className="mt-2 px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
          >
            {dimensions.map((dim) => (
              <option key={dim} value={dim}>{formatLabel(dim)}</option>
            ))}
          </select>
        </label>
      </div>

      {/* Scatter Chart */}
      <ResponsiveContainer width="100%" height={500}>
        <ScatterChart margin={{ top: 20, right: 30, left: 30, bottom: 20 }}>
          {/* Grid */}
          <CartesianGrid strokeDasharray="3 3" stroke="#A0AEC0" />

          {/* X-Axis */}
          <XAxis 
            type="number" 
            dataKey={xAxis} 
            name={xAxis} 
            domain={[0, 1]} 
            tick={{ fontSize: 14, fill: "#182451" }}
            tickFormatter={formatPercentage} // Format X-axis tick values
          >
            <Label value={formatLabel(xAxis)} offset={-10} position="insideBottom" fontSize={14} fill="#182451" />
          </XAxis>

          {/* Y-Axis */}
          <YAxis 
            type="number" 
            dataKey={yAxis} 
            name={yAxis} 
            domain={[0, 1]} 
            tick={{ fontSize: 14, fill: "#182451" }}
            tickFormatter={formatPercentage} // Format Y-axis tick values
          >
            <Label value={formatLabel(yAxis)} angle={-90} position="insideLeft" fontSize={14} fill="#182451" />
          </YAxis>

          {/* Tooltip */}
          <Tooltip
            cursor={{ strokeDasharray: "3 3", stroke: "#6B7280", strokeWidth: 1 }}
            contentStyle={{ backgroundColor: "#ffffff", border: "1px solid #D1D5DB", borderRadius: "4px", padding: "8px" }}
            formatter={(value) => formatPercentage(value)} // Format tooltip values
            labelFormatter={(label) => typeof label === 'string' ? formatLabel(label) : label} // Safely handle label
        />

          {/* Scatter Data with custom point color */}
          <Scatter name="Feeds" data={feeds} fill="#182451" shape="circle" />

          {/* Linear Regression Line */}
          <Line 
            type="monotone" 
            data={lineData} 
            dataKey={yAxis} 
            stroke="#FF5733" 
            strokeWidth={2} 
            dot={false} 
            activeDot={false} 
          />

          {/* Center Lines for Quadrants */}
          <CartesianGrid vertical={false} stroke="black" strokeWidth={1} />
          <CartesianGrid horizontal={false} stroke="black" strokeWidth={1} />
        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PoliticalCompass;
