import React, { useState } from "react";
import { ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Label } from "recharts";

const PoliticalCompass = ({ feeds }) => {
  const [xAxis, setXAxis] = useState("toxicity_score");
  const [yAxis, setYAxis] = useState("positivity_score");

  const dimensions = ["likes", "posts", "toxicity_score", "positivity_score", "commercial_link_score", "poster_diversity_score"];

  const formatLabel = (label) => {
    if (typeof label !== 'string') return label;
    return label
      .split('_')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join(' ');
  };

  const formatPercentage = (value) => {
    if (value !== undefined) {
      return `${(value * 100).toFixed(2)}%`;
    }
    return "N/A";
  };

  return (
    <div className="p-4 border rounded-lg bg-white shadow-md">
      <h2 className="text-xl font-semibold mb-4">Feed Political Compass</h2>

      <div className="flex gap-4 mb-4">
        <label>
          X-Axis:{" "}
          <select value={xAxis} onChange={(e) => setXAxis(e.target.value)} className="border p-2 rounded">
            {dimensions.map((dim) => (
              <option key={dim} value={dim}>{formatLabel(dim)}</option>
            ))}
          </select>
        </label>

        <label>
          Y-Axis:{" "}
          <select value={yAxis} onChange={(e) => setYAxis(e.target.value)} className="border p-2 rounded">
            {dimensions.map((dim) => (
              <option key={dim} value={dim}>{formatLabel(dim)}</option>
            ))}
          </select>
        </label>
      </div>

      <ResponsiveContainer width="100%" height={500}>
        <ScatterChart margin={{ top: 20, right: 30, left: 30, bottom: 20 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="#B0B0B0" />
          <XAxis type="number" dataKey={xAxis} name={xAxis} domain={[-1, 1]} tick={{ fontSize: 14, fill: "#182451" }}>
            <Label value={formatLabel(xAxis)} offset={-10} position="insideBottom" fontSize={14} fill="#182451" />
          </XAxis>
          <YAxis type="number" dataKey={yAxis} name={yAxis} domain={[-1, 1]} tick={{ fontSize: 14, fill: "#182451" }}>
            <Label value={formatLabel(yAxis)} angle={-90} position="insideLeft" fontSize={14} fill="#182451" />
          </YAxis>
          <Tooltip cursor={{ strokeDasharray: "3 3", stroke: "#6B7280", strokeWidth: 1 }} 
            contentStyle={{ backgroundColor: "#ffffff", border: "1px solid #D1D5DB", borderRadius: "4px", padding: "8px" }}
            formatter={(value) => formatPercentage(value)} 
            labelFormatter={(label) => typeof label === 'string' ? formatLabel(label) : label} 
          />
          <Scatter name="Feeds" data={feeds} fill="#182451" />
        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PoliticalCompass;
