import React from 'react';

const getColorIndicator = (value, type) => {
  // Define thresholds for each metric type
  if (type === "toxicity") {
    return value > 0.5 ? 'red' : 'green'; // Red for bad toxicity, green for good
  }
  if (type === "positivity") {
    return value > 0.7 ? 'green' : 'red'; // Green for good positivity, red for bad
  }
  return 'yellow'; // Default for other types
};

const Comparison = ({ feed1, feed2 }) => {
  return (
    <div className="flex justify-between p-4">
      <div className="flex-1 border p-4">
        <h2 className="text-xl font-bold mb-4">Feed 1</h2>
        <div>
          <div className="mb-2">
            <span>Toxicity Score: {feed1.toxicity_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed1.toxicity_score, 'toxicity')}`}
            ></span>
          </div>
          <div className="mb-2">
            <span>Positivity Score: {feed1.positivity_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed1.positivity_score, 'positivity')}`}
            ></span>
          </div>
          <div className="mb-2">
            <span>Commercial Link Score: {feed1.commercial_link_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed1.commercial_link_score, 'neutral')}`}
            ></span>
          </div>
          <div className="mb-2">
            <span>Poster Diversity Score: {feed1.poster_diversity_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed1.poster_diversity_score, 'neutral')}`}
            ></span>
          </div>
        </div>
      </div>

      <div className="flex-1 border p-4">
        <h2 className="text-xl font-bold mb-4">Feed 2</h2>
        <div>
          <div className="mb-2">
            <span>Toxicity Score: {feed2.toxicity_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed2.toxicity_score, 'toxicity')}`}
            ></span>
          </div>
          <div className="mb-2">
            <span>Positivity Score: {feed2.positivity_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed2.positivity_score, 'positivity')}`}
            ></span>
          </div>
          <div className="mb-2">
            <span>Commercial Link Score: {feed2.commercial_link_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed2.commercial_link_score, 'neutral')}`}
            ></span>
          </div>
          <div className="mb-2">
            <span>Poster Diversity Score: {feed2.poster_diversity_score}</span>
            <span
              className={`ml-2 w-4 h-4 rounded-full inline-block ${getColorIndicator(feed2.poster_diversity_score, 'neutral')}`}
            ></span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Comparison;
